<?phpnamespace config;class DbConnection{ private $host = 'localhost'; private $username = 'root'; private  $password = ''; private $DbName = 'tescapstone'; public $conn; public function Connect() {     $this->conn = new \mysqli($this->host,$this->username,$this->password,$this->DbName);     if ($this->conn->connect_error){         die("Connection problem");     }     return $this->conn; }}