<?phpnamespace controller;use model\EmployeeSalesModel;include '../model/EmployeeSalesModel.php';class EmployeeSaleController{  public function DisplayEmployeeSales()  {      $startDate = $_POST['startDate'];      $endDate = $_POST['endDate'];      if (empty($startDate) && empty($endDate)){          echo "<script>                Swal.fire({                text: 'Please select a date range.',                confirmButtonColor: '#4361ee',                iconColor: '#ff6347',                icon: 'warning',                toast: true,                target: 'body',                position: 'top-end',                showConfirmButton: false,                timer: 2000,  // Auto-close the alert after 2 seconds                timerProgressBar: true,  // Show the progress bar for the timer            });          </script>";      }else{          $model = new EmployeeSalesModel();          $model->EmployeeSales($startDate,$endDate);      }  }  public function DisplayDefault()  {      $modal = new EmployeeSalesModel();      $modal->DefaultDisplay();  }}if ($_SERVER['REQUEST_METHOD'] === 'POST'){    $controller = new EmployeeSaleController();    $controller->DisplayEmployeeSales();}