<?phpnamespace controller;use model\UserModel;include '../model/UserModel.php';class UserController{    public function DisplayUser()    {       $model = new UserModel();       $model->DisplayUser();    }    public function CreateUser()    {        $username = $_POST['username'];        $password = $_POST['password'];        $branch = $_POST['BranchName'];        $hashedPassword = password_hash($password,PASSWORD_BCRYPT);        $data = [];        $error = [];        if (empty($username)){            $error['username'] = 'username is required';        }        if (empty($password)){            $error['password'] = 'password is required';        }        if (!empty($error)){            $data['success'] = false;            $data['error'] = $error;            echo json_encode($data);        }else{            $model = new UserModel();            $model->CreateUserCredentials($username,$hashedPassword,$branch);        }    }    public function UpdateUser()    {        $id = $_POST['id'];        if (isset($id)){            $model = new UserModel();            $model->UpdateUser($id);        }else{            echo json_encode('No ID');        }    }    public function SaveUpdateUser()    {        $model = new UserModel();        if (isset($_POST['id'])){              $id = $_POST['id'];              $userName = $_POST['SaveUsername'];              $password = $_POST['SavePassword'];              if(!empty($password)){                  $passHashed = password_hash($password,PASSWORD_BCRYPT);                  $model->SaveUser($userName,$passHashed,$id);              }else{                  $model->SaveUserNameId($userName,$id);              }        }    }    public function ArchiveUser()    {        $id = $_POST['id'];        $modal = new UserModel();        $modal->ArchiveUser($id);    }    public function SumUser()    {        $modal = new UserModel();        $modal->DisplaySum();    }} if ($_SERVER['REQUEST_METHOD'] === 'POST'){     if (isset($_POST['action'])){         $controller = new UserController();         switch ($_POST['action']){             case 'create':                 $controller->CreateUser();                 break;             case 'Update':                 $controller->UpdateUser();                 break;             case 'SaveUser':                 $controller->SaveUpdateUser();                 break;             case 'ArchiveUser':                 $controller->ArchiveUser();                 break;         }     } }