<?phpnamespace controller;use model\authModel;include_once '../model/authModel.php';class authController{  public  function AdminLogin()  {       $username = $_POST['username'];       $password = $_POST['password'];       $data = [];       $error = [];       if (empty($username)){           $error['username'] = 'Please enter your username to proceed.';       }       if (empty($password)){           $error['password'] = 'Please enter your password to proceed.';       }       if (!empty($error)){           $data[''] = false;           $data['error'] = $error;           echo json_encode($data);       }else{           $auth = new authModel();           $auth->adminLogin($username,$password);       }  }}if ($_SERVER['REQUEST_METHOD'] === 'POST' && $_POST['action']){    $controller = new authController();    switch ($_POST['action']){        case 'adminLogin':           $controller->AdminLogin();            break;    }}