<?phpnamespace controller;include_once '../model/employeeViewModel.php';use model\employeeViewModel;class employeeViewController{    public function showProductInfo()    {        $model = new employeeViewModel();        $productId = $_POST['productId'];        if (isset($productId)){            $data = $model->showProductInfoBaseOnId($productId);            echo json_encode($data);        }    }}if($_SERVER['REQUEST_METHOD'] === 'POST' && $_POST['action']){    $controller = new employeeViewController();    switch ($_POST['action']){        case 'showProductInfo':            $controller->showProductInfo();            break;    }}