<?phpnamespace controller;use model\logs;require_once '../model/logs.php';class logsController{public static function showLogs(){    $logs = new logs();    $listOfLogs = $logs->showAll();    $tr_table = '';    if ($listOfLogs){        foreach ($listOfLogs as $log){          $tr_table .= '           <tr>             <th scope="row">'.$log['id'].'</th>             <td>'.$log['username'].'</td>             <td>'.$log['action'].'</td>             <td>'.$log['details'].'</td>             <td>'.$log['date'].'</td>            </tr>          ';        }        echo $tr_table;    }}}