<?phpnamespace model;use config\DbConnection;include_once '../config/DbConnection.php';class EmployeeSalesModel extends  DbConnection{  public function EmployeeSales($startDate,$EndDate)  {   $Query = "       SELECT s.EmployeeName,s.branch, p.product_name, SUM(s.quantity_sold) AS total_quantity_sold        FROM sales s                 JOIN product p ON s.product_id = p.product_id        WHERE s.Sales_date BETWEEN ? AND ?        GROUP BY s.EmployeeName, p.product_name        ORDER BY total_quantity_sold DESC   ";      $stmt = $this->Connect()->prepare($Query);      $stmt->bind_param('ss',$startDate,$EndDate);      $stmt->execute();      $result = $stmt->get_result();      $tr = '';      if ($result->num_rows > 0){          while ($row = $result->fetch_assoc()){              $tr .= '               <tr>               <th>'.$row['EmployeeName'].'</th>               <td>'.$row['branch'].'</td>               <td>'.$row['product_name'].'</td>               <td>'.$row['total_quantity_sold'].'</td>               </tr>              ';          }      }else{          $tr .= '          <script>           Swal.fire({                text: "No Result Fund",                confirmButtonColor: "#4361ee",                iconColor: "#ff6347",                icon: "warning",                toast: true,                target: "body",                position: "top-end",                showConfirmButton: false,                timer: 2000,  // Auto-close the alert after 2 seconds                timerProgressBar: true,  // Show the progress bar for the timer            });         </script>          ';      }      echo $tr;      $stmt->close();      $this->Connect()->close();  }    public function DefaultDisplay()    {        $result = $this->Connect()->query("SELECT s.EmployeeName,s.branch, p.product_name, SUM(s.quantity_sold) AS total_quantity_sold        FROM sales s                 JOIN product p ON s.product_id = p.product_id        GROUP BY s.EmployeeName, p.product_name        ORDER BY total_quantity_sold DESC");        $tr = '';        if ($result->num_rows > 0){            while ($row = $result->fetch_assoc()){                $tr .= '               <tr>               <th>'.$row['EmployeeName'].'</th>               <td>'.$row['branch'].'</td>               <td>'.$row['product_name'].'</td>               <td>'.$row['total_quantity_sold'].'</td>               </tr>              ';            }        }else{            $tr .= '              <tr>                <th>No Sales Available</th>              </tr>          ';        }        echo $tr;    }}