<?phpnamespace model;use config\DbConnection;include_once '../config/DbConnection.php';class LowStackModel extends DbConnection{    public function DisplayLowStack()    {        $result = $this->Connect()->query("SELECT DISTINCT product_name, current_stack, item_color,product_id, branch FROM product WHERE current_stack <= 5 AND Status = 'Active' ORDER BY current_stack");        $dataRow = [];        if ($result->num_rows > 0){            while ($row = $result->fetch_assoc()){                $dataRow[] = $row;            }            return $dataRow;        }        return  null;    }    public function updateStack($id)    {        $Query  = "select current_stack,product_id from product where product_id = ?";        $stmt = $this->Connect()->prepare($Query);        $stmt->bind_param('i',$id);        $stmt->execute();        $result = $stmt->get_result();        $dataRow = [];        if ($result->num_rows > 0){            while ( $row = $result->fetch_assoc()){                $dataRow[] = $row;            }            return $dataRow;        }else{            return  null;        }    }    public function saveUpdatedStack($stack,$id)    {        $query = "UPDATE product SET current_stack = ? WHERE product_id = ?";        $stmt = $this->Connect()->prepare($query);        $stmt->bind_param('si',$stack,$id);        if ($stmt->execute()){            echo json_encode(['success' => true , 'message' => 'Successfully Updated']);        }else{            echo json_encode(['success' => false , 'error' => $stmt->error]);        }    }}