<?phpnamespace model;use config\DbConnection;session_start();include_once '../config/DbConnection.php';class authModel extends DbConnection{    public  function adminLogin($userName,$password)    {        $query = "SELECT * FROM admin where username = ?";        $stmt = $this->Connect()->prepare($query);        $stmt->bind_param('s',$userName);        $stmt->execute();        $result = $stmt->get_result();        if ($result->num_rows > 0){            $admin = $result->fetch_assoc();            if (password_verify($password,$admin['password'])){                echo json_encode(['success' => true ,'message' => 'successfully logged']);                $_SESSION['admin'] = $userName;            }else{                echo json_encode(['success' => false, 'message' => 'invalid password or username']);            }        }else{            echo json_encode(['success' => false, 'message' => 'Account Not Found']);        }    }    // login for employee per branch    public function employeeLogin($password,$username)    {        $query = "SELECT * FROM employee WHERE username = ?";        $stmt = $this->Connect()->prepare($query);        $stmt->bind_param('s',$username);        $stmt->execute();        $result = $stmt->get_result();        if ($result->num_rows > 0){            $employee = $result->fetch_assoc();            if (password_verify($password,$employee['password'])){                echo json_encode(['success' => true ,'message' => 'successfully logged']);                $_SESSION['employee'] = $username;                $_SESSION['branch'] = $employee['Branch'];             }else{                echo json_encode(['success' => false, 'message' => 'invalid password or username']);            }        }else{            echo json_encode(['success' => false, 'message' => 'Account Not Found']);        }    }}