<?phpnamespace model;use config\DbConnection;include_once '../config/DbConnection.php';class employeeViewModel extends DbConnection{    public function showProductInfoBaseOnId($productId)    {        $query = "SELECT branch,current_stack,unit_price,product_id FROM product WHERE product_id = ? AND  Status = 'Active'";        $stmt = $this->Connect()->prepare($query);        $stmt->bind_param('i',$productId);        $stmt->execute();        $result = $stmt->get_result();        if ($result->num_rows > 0){            $dataRow = [];            while ($row = $result->fetch_assoc()){                $dataRow[] = $row;            }            return $dataRow;        }        return  null;    }}