<?phpnamespace model;use config\DbConnection;use Exception;require_once '../config/DbConnection.php';class logs extends DbConnection{  public function makeLogs($username,$action,$details)  {      try {          $query = "INSERT INTO logs(username, action, details) VALUES (?,?,?)";          $stmt = $this->Connect()->prepare($query);          if (!$stmt){              echo json_encode(['success' => false, 'message' => 'Failed to Prepared Statement']);              return;          }          $stmt->bind_param('sss',$username,$action,$details);           $stmt->execute();      }catch (Exception $e){          error_log('Error'.$e->getMessage());      }  }  public function showAll()  {      try {          $result = $this->Connect()->query("SELECT * FROM logs");          $data = [];          if ($result->num_rows > 0){                while ($row = $result->fetch_assoc()){                    $data[] = $row;                }          }          return $data;      }catch (Exception $e){          error_log('Error'.$e->getMessage());          return [];    }  }}