<?phpnamespace model;use config\DbConnection;include_once '../config/DbConnection.php';class recycle_bin extends DbConnection{public function showAllDeletedData($table){    $table = preg_replace('/[^a-zA-Z0-9_]/', '', $table);    $query = "SELECT * FROM $table WHERE status != 'Active'";    $stmt = $this->Connect()->prepare($query);    $stmt->execute();    $result = $stmt->get_result();    if($result->num_rows > 0){        $data = [];        while ($row = $result->fetch_assoc()){            $data[] = $row;        }        return $data;    }    return [];}public function restoreData($table,$id){  $query = "UPDATE $table SET status = 'Active' WHERE id = ?";  $stmt = $this->Connect()->prepare($query);  $stmt->bind_param('i',$id);  if ($stmt->execute()){      echo json_encode(['success' => true, 'message' => 'Successfully Restored']);  }else{      echo json_encode(['success' => false, 'message' => 'Error'.$stmt->error]);  }}}