<?phpsession_start();if (isset($_SESSION['admin'])){    header('Location: ./Dashboard.php');}?><!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <link rel="stylesheet" href="../../css/bootstrap.min.css">    <script src="../../js/bootstrap.bundle.min.js"></script>    <link href="https://fonts.cdnfonts.com/css/ninja-naruto" rel="stylesheet">    <script src="https://cdn.jsdelivr.net/npm/sweetalert2@11"></script>    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>    <title>adminLogin</title>    <style>        h1{            font-family: 'Ninja Naruto', sans-serif;        }    </style></head><body  class="bg-dark"><main style="width: 100%; height: 100vh;" class="d-flex flex-column justify-content-center align-items-center">    <form id="adminLoginForm" method="post" action="../controller/authController.php" class="d-flex flex-column gap-3 col-lg-3 text-light">            <h1 style="color: #FFFF" class="fw-bold">SIGN IN</h1>            <div>                <label for="username">                    UserName                </label>                <input type="text" class="form-control" name="username" id="username" placeholder="Enter UserName">               <div id="usernameMSG" class="invalid-feedback"></div>            </div>            <div>                <label for="password">                    Password                </label>                <input type="password" class="form-control" name="password" id="password" placeholder="Enter password">                <div id="passwordMSG" class="invalid-feedback"></div>            </div>                <button style="background-color: #1c3f7c" type="submit" class="btn text-light">Login</button>    </form></main><!-- Modal --></body></html><script>$(document).ready(function (){    $(document).on('submit','#adminLoginForm',function (event){        event.preventDefault();        const formData = new FormData(this)        formData.append('action','adminLogin')        $.ajax({            url: '../controller/authController.php',            type: 'post',            data: formData,            contentType: false,            processData: false,            dataType: 'json',            success: function (res){               if (res.error){                     // helper funtion                   function displayError(fild,message){                       if (message){                           $(`#${fild}MSG`).html(message)                           $(`#${fild}`).addClass('is-invalid')                       }else {                           $(`#${fild}MSG`).html('')                           $(`#${fild}`).removeClass('is-invalid')                       }                   }                   if (res.error){                       displayError('username',res.error.username)                       displayError('password',res.error.password)                   }               }                if (res.success === true){                    const Toast = Swal.mixin({                        toast: true,                        position: "top-end",                        showConfirmButton: false,                        timer: 2000,                        timerProgressBar: true,                        didOpen: (toast) => {                            toast.onmouseenter = Swal.stopTimer;                            toast.onmouseleave = Swal.resumeTimer;                        }                    });                    Toast.fire({                        icon: "success",                        text: res.message                    });                    setTimeout(() => {window.location.href = './Dashboard.php'},2000)                }                if (res.success === false ) {                    const Toast = Swal.mixin({                        toast: true,                        position: "top-end",                        showConfirmButton: false,                        timer: 2000,                        timerProgressBar: true,                        didOpen: (toast) => {                            toast.onmouseenter = Swal.stopTimer;                            toast.onmouseleave = Swal.resumeTimer;                        }                    });                    Toast.fire({                        icon: "error",                        text: res.message                    });                }            }        })    })})</script>