<?phpsession_start();if (isset($_SESSION['admin'])){    header('Location: ./Dashboard.php');}?><!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <link rel="stylesheet" href="../../css/bootstrap.min.css">    <script src="../../js/bootstrap.bundle.min.js"></script>    <script src="https://cdn.jsdelivr.net/npm/sweetalert2@11"></script>    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>    <title>adminLogin</title></head><body  class="bg-dark"><main style="width: 100%; height: 100vh;" class="d-flex flex-column justify-content-center align-items-center">    <form id="adminLoginForm" method="post" action="../controller/authController.php" class="d-flex flex-column gap-3 col-lg-3 col-sm-10 text-light">            <h2 class="fw-bold"> <span class="text-primary">Wecome</span> Back</h2>        <div class="d-flex flex-column gap-1">                <label for="username">                    UserName                </label>               <input type="text" class="form-control p-3 border-0" name="username" id="username" placeholder="Enter UserName">               <div id="usernameMSG" class="invalid-feedback"></div>             </div>        <div class="d-flex flex-column gap-1">            <label for="password">Password</label>            <input type="password" class="form-control p-3 border-0" name="password" id="password" placeholder="Enter password">            <div id="passwordMSG" class="invalid-feedback"></div>            <!-- Show Password Checkbox -->            <div class="form-check">                <input class="form-check-input" type="checkbox" id="showPassword">                <label class="form-check-label" for="showPassword">                    Show Password                </label>            </div>        </div>           <button id="btn_login"  type="submit" class="btn btn-primary btn-lg">               Login           </button>    </form></main><!-- Modal --></body></html><script>    // showing password    document.getElementById('showPassword').addEventListener('change',function (){        const passwordInput =  document.getElementById('password');        passwordInput.type = this.checked ? 'text' : 'password';    })</script><script>$(document).ready(function (){    $(document).on('submit','#adminLoginForm',function (event){        event.preventDefault();        const formData = new FormData(this)        formData.append('action','adminLogin')        $.ajax({            url: '../controller/authController.php',            type: 'post',            data: formData,            contentType: false,            processData: false,            dataType: 'json',            success: function (res){               if (res.error){                     // helper funtion                   function displayError(fild,message){                       if (message){                           $(`#${fild}MSG`).html(message)                           $(`#${fild}`).addClass('is-invalid')                       }else {                           $(`#${fild}MSG`).html('')                           $(`#${fild}`).removeClass('is-invalid')                       }                   }                   if (res.error){                       displayError('username',res.error.username)                       displayError('password',res.error.password)                   }                   return               }               $('#btn_login').html(                   `                   <button class="btn btn-primary" type="button" disabled>                      <span class="spinner-border spinner-border-sm" aria-hidden="true"></span>                      <span class="visually-hidden" role="status">Loading...</span>                    </button>                   `               );                if (res.success === true){                    setTimeout(() => {window.location.href = './Dashboard.php'},2000)                }                if (res.success === false ) {                    const Toast = Swal.mixin({                        toast: true,                        position: "top-end",                        showConfirmButton: false,                        timer: 2000,                        timerProgressBar: true,                        didOpen: (toast) => {                            toast.onmouseenter = Swal.stopTimer;                            toast.onmouseleave = Swal.resumeTimer;                        }                    });                    Toast.fire({                        icon: "error",                        text: res.message                    });                    $('#btn_login').html(                        `Login`                    );                }            }        })    })})</script>