<!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <title>Low Stack List</title>    <link rel="stylesheet" href="../../css/bootstrap.min.css">    <link rel="stylesheet" href="../../style.css">    <link rel="stylesheet" href="../../table.css">    <script src="../../js/bootstrap.bundle.min.js"></script>    <link rel="stylesheet" type="text/css" href="https://cdn.datatables.net/1.11.5/css/jquery.dataTables.css">    <!-- Include jQuery -->    <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>    <!-- Include DataTables JS -->    <script type="text/javascript" charset="utf8" src="https://cdn.datatables.net/1.11.5/js/jquery.dataTables.js"></script>    <!--    ajax cdn-->    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>    <!--    cdn sweet alert-->    <script src="https://cdn.jsdelivr.net/npm/sweetalert2@11"></script>    <!--    icon-->    <script src="https://kit.fontawesome.com/d4532539ca.js" crossorigin="anonymous"></script></head><body ><header style="background-color: #4361ee" class="container-fluid d-flex justify-content-between align-items-center p-3">    <nav aria-label="breadcrumb">        <ol class="breadcrumb">            <li class="breadcrumb-item"><a class="text-light" href="./Dashboard.php">Dashboard</a></li>            <li class="breadcrumb-item active text-light" aria-current="page">Low Stack List</li>        </ol>    </nav></header><section class="p-4">    <table id="BranchTable" class="table table-hover table-responsive-sm table-striped">        <thead>        <tr>            <th>#</th>            <th>Product Name</th>            <th>Current Stack</th>            <th>Item Color</th>            <th>Branch</th>            <th>Action</th>        </tr>        </thead>        <tbody>        </tbody>    </table></section><script>    let table = new DataTable('#BranchTable',{        scrollCollapse: true,        scrollY: '50vh',        destroy: true,        paging: true,        searching: true,        ordering: true    });</script><script>    $(document).ready(function (){        $('#EmployeeSaleForm').submit(function (event){            event.preventDefault();            const formData = new FormData(this);            $.ajax({                url:'../controller/EmployeeSaleController.php',                type: 'POST',                data: formData,                processData: false,                contentType: false,                success: function (data){                    $('tbody').html(data)                }            })        })    })</script></body></html>