        </div>                </div>                <div>                    <label for="Branch">                        Select Branch                    </label>                    <select name="BranchName" id="choice_branch" class="form-select" aria-label="Default select example">                    </select>                </div>                <div class="modal-footer">                    <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Close</button>                    <button id="btn_createAccount"  type="submit" class="btn btn-primary">                        Create Now                    </button>                </div>            </form>        </div>    </div>    <script src="../../AJAX/UserManagement.js"></script></body></html><script>    const ConfirmPass = document.getElementById('ConfirmPassword');    const password = document.getElementById('password');    const btn_createAccount = document.getElementById('btn_createAccount');    ConfirmPass.addEventListener('keyup',function (){        if (password.value !== ConfirmPass.value){            document.getElementById('ConfirmPassError').innerText = 'Passwords do not match. Please try again!';            ConfirmPass.classList.add('is-invalid')            btn_createAccount.disabled = true        }else {            document.getElementById('ConfirmPassError').innerText = '';            ConfirmPass.classList.remove('is-invalid')            btn_createAccount.disabled = false        }    })</script>