<!DOCTYPE html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1.0">    <title>Show Password with Bootstrap</title>    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet"></head><body class="p-4"><div class="d-flex flex-column gap-1">    <label for="password">Password</label>    <input type="password" class="form-control p-3 border-0" name="password" id="password" placeholder="Enter password">    <div id="passwordMSG" class="invalid-feedback"></div>    <!-- Show Password Checkbox -->    <div class="form-check">        <input class="form-check-input" type="checkbox" id="showPassword">        <label class="form-check-label" for="showPassword">            Show Password        </label>    </div></div><script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script><script>    document.getElementById('showPassword').addEventListener('change', function () {        let passwordInput = document.getElementById('password');        passwordInput.type = this.checked ? 'text' : 'password';    });</script></body></html>